export const Previews: React.FC<Props> = ({
  children,
  palette = null,
}: Props) => {
  const [toolsPropsToEdit, setToolsPropsToEdit] =
    useState<ToolsPropsModifier>(null);
  const [toolsPanelEnabled, enableToolsPanel] = useState<boolean>(
    () => window.__PROPERTIES_EDIT_PANEL_ENABLED__ ?? false,
  );
  const isPalettePath = useRoute(PALETTE_PATH);

  const childrenWithSetProps = useMemo(() => {
    return children
      ? React.Children.map(children, (child: JSX.Element) => {
          return React.cloneElement(child, {setToolsPropsToEdit});
        })
      : null;
  }, [children]);

  useEffect(() => {
    window.enableComponentsPropsPanelEditor = (toolsPanelStatus: boolean) => {
      window.setPropertiesEditPanelStatus?.(toolsPanelStatus);
      enableToolsPanel(toolsPanelStatus);
    };

    window.reactBuddyHistoryPush = historyPush;
    window.cefQuery?.({request: 'event:react-toolbox-initialized'});
  }, []);

  if (isPalettePath) {
    return palette;
  }

  return (
    <div className={styles.previewsMain}>
      <div className={styles.previewsContent}>{childrenWithSetProps}</div>
      {toolsPanelEnabled && (
        <div className={styles.previewsToolsPanel}>
          <ToolsPanel toolsPropsToEdit={toolsPropsToEdit} />
        </div>
      )}
    </div>
  );
};
import React, {useEffect, useState} from 'react';
import {ComponentPreviewProps, PropsModifier} from '@types';
import {RouteProps, useRoute} from '../routing/routing';

type RoutePreviewProps = RouteProps & {
  setPropsToEdit: (propsModifier: PropsModifier) => void;
};

export const RoutePreview: React.FC<RoutePreviewProps> = ({
  children,
  path,
  exact,
  setPropsToEdit,
}) => {
  const [modfiedProps, setModifiedProps] =
    useState<ComponentPreviewProps>(null);

  const isMatchPath = useRoute(path, exact);

  useEffect(() => {
    if (isMatchPath) {
      const currentProps = modfiedProps ?? children.props;
      const propsToEdit = {
        props: currentProps,
        updateProps: setModifiedProps,
      };
      setPropsToEdit(propsToEdit);
    }

    return () => {
      isMatchPath ? null : setModifiedProps(null);
    };
  }, [isMatchPath]);

  const childrenWithUpdatedProps = modfiedProps
    ? React.cloneElement(children, {...modfiedProps})
    : children;

  return isMatchPath ? <>{childrenWithUpdatedProps}</> : null;
};
